-- Naprawa opisów roślin - Batch 2 (ID 429-528)
-- Profesjonalny, spójny styl polski

UPDATE plants SET notes = 'Wymaga kwaśnej gleby. Przy pH 4,5-5,5 kwiaty są niebieskie, powyżej pH 6 – różowe. Stosować nawozy zakwaszające. Obficie podlewać. Cięcie wiosną (marzec-kwiecień). Młode rośliny okrywać na zimę. Nie stosować popiołu drzewnego.' WHERE id = 429;

UPDATE plants SET notes = 'Odmiana powtarzająca kwitnienie – kwitnie dwukrotnie: w maju-czerwcu i we wrześniu. Kłącze musi być odsłonięte – nie zasypywać ziemią. Optymalne pH 6,5-7,5. Dzielić co 3-4 lata w lipcu-sierpniu. Usuwać zwiędłe kwiaty.' WHERE id = 430;

UPDATE plants SET notes = 'Odmiana koralowo-różowa o intensywnym zapachu. Kłącze pozostawić odsłonięte. Dzielić co 3-4 lata. Optymalne pH 6,5-7,5. Preferuje suche stanowisko – nie znosi podmokłych korzeni.' WHERE id = 431;

UPDATE plants SET notes = 'Dwukolorowa odmiana żółto-biała. W odróżnieniu od irysów bródkowych toleruje wilgotne gleby, można sadzić przy oczku wodnym. Dzielić co 4-5 lat. Bardzo mrozoodporna.' WHERE id = 432;

UPDATE plants SET notes = 'Odmiana o ogromnych liściach do 50 cm. Złotożółte, grube liście odporne na żerowanie ślimaków. Toleruje słońce lepiej niż inne odmiany funkii. Ściółkować. Dzielenie co 5 lat lub rzadziej. Chronić przed ślimakami.' WHERE id = 433;

UPDATE plants SET notes = 'Ciemnozielone liście z szeroką białą obwódką. Bardzo popularna odmiana. Przed ślimakami chronić piaskiem, pułapkami piwnymi lub trocinami. Ściółkować. Dzielić co 4-5 lat.' WHERE id = 434;

UPDATE plants SET notes = 'Niebiesko-zielone liście z szeroką żółtą obwódką. Odmiana wielokrotnie nagradzana. Powolny wzrost. Chronić przed ślimakami. Ściółkować.' WHERE id = 435;

UPDATE plants SET notes = 'Najpopularniejszy liliowiec na świecie. Odmiana powtarzająca kwitnienie – kwitnie przez całe lato. Roślina kompaktowa, bardzo tolerancyjna wobec warunków. Dzielić co 3-4 lata. Usuwać zwiędłe kwiaty.' WHERE id = 436;

UPDATE plants SET notes = 'Odmiana powtarzająca kwitnienie, cytrynowa, pachnąca. Roślina kompaktowa, bardzo tolerancyjna. Dzielić co 3-4 lata. Usuwanie zwiędłych kwiatów przedłuża kwitnienie.' WHERE id = 437;

UPDATE plants SET notes = 'Odmiana ciemnoczerwona, kompaktowa, powtarzająca kwitnienie. Pachnąca, bardzo popularna. Dzielić co 3-4 lata. Roślina tolerancyjna wobec różnych warunków.' WHERE id = 438;

UPDATE plants SET notes = 'Odmiana odporna na mączniaka prawdziwego. Czysto białe, pachnące kwiaty. Dzielić co 3-4 lata. Obficie podlewać. Ściółkować. Przycinanie po kwitnieniu stymuluje drugą falę kwitnienia. Zapewnić przewiewne stanowisko.' WHERE id = 439;

UPDATE plants SET notes = 'Intensywnie czerwone kwiaty na ciemnych łodygach. Dzielić co 3-4 lata. Podlewać u podstawy rośliny, unikając zamakania liści. Ściółkować. Przewiewne stanowisko zapobiega mączniakowi.' WHERE id = 440;

UPDATE plants SET notes = 'Gatunek botaniczny o właściwościach leczniczych. Bardzo tolerancyjna – znosi suszę i ubogą glebę. Rozmnaża się z samosiewu. Pozostawić przekwitnięte na zimę jako pokarm dla ptaków. Dzielić co 4-5 lat. Może wystąpić rdza.' WHERE id = 441;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 1998. Płatki płaskie, nieopadające. Duże kwiaty, roślina bardzo tolerancyjna. Pozostawić przekwitnięte na zimę jako pokarm dla ptaków.' WHERE id = 442;

UPDATE plants SET notes = 'Klasyczna biała odmiana jeżówki. Roślina kompaktowa, tolerancyjna. Rozmnaża się z samosiewu. Pozostawić na zimę dla ptaków.' WHERE id = 443;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 1999. Najpopularniejsza rudbekia na świecie. Długi okres kwitnienia od lipca do października. Bardzo tolerancyjna. Dzielić co 3-4 lata. Pozostawić przekwitnięte na zimę dla ptaków.' WHERE id = 444;

UPDATE plants SET notes = 'Kompaktowa wersja odmiany Goldsturm. Idealna do małych ogrodów i pojemników. Obficie kwitnąca, bardzo tolerancyjna.' WHERE id = 445;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 2000. Pokrój kompaktowy, nie wymaga podpór. Obfite jesienne kwitnienie. Odporna na mączniaka prawdziwego. Dzielić co 2-3 lata. Przycinanie w czerwcu wspomaga krzewienie.' WHERE id = 446;

UPDATE plants SET notes = 'Intensywnie różowa odmiana o wysokim wzroście wymagającym podpór. Odporna na mączniaka. Dzielić co 2-3 lata. Przycinanie w czerwcu wspomaga krzewienie. Królowa jesiennych rabat.' WHERE id = 447;

UPDATE plants SET notes = 'Kompaktowa seria niewymagająca podpór. Przycięcie po pierwszym kwitnieniu stymuluje drugą falę we wrześniu. Obficie podlewać. Regularnie nawozić. Chronić przed ślimakami. Nie znosi upałów powyżej 25°C.' WHERE id = 448;

UPDATE plants SET notes = 'Imponująca wysokość do 180 cm. Wymaga mocnych podpór. Przycięcie po kwitnieniu stymuluje drugą falę. Wymaga bardzo żyznej gleby oraz obfitego nawożenia i podlewania. Chronić przed ślimakami i silnym wiatrem.' WHERE id = 449;

UPDATE plants SET notes = 'Kompaktowa seria. Rośliny wiążą azot – nie nawozić. Optymalne pH 5,5-6,5. Przycięcie po kwitnieniu stymuluje drugą falę w sierpniu. Roślina krótko żyjąca (2-3 lata), ale rozmnaża się z samosiewu. Uwaga: roślina trująca.' WHERE id = 450;

UPDATE plants SET notes = 'Klasyczna seria Russell z 1937 roku o imponujących kwiatostanach. Rośliny wiążą azot. Przycinać po kwitnieniu. Roślina krótko żyjąca, ale rozmnaża się z samosiewu. Uwaga: trująca dla ludzi i zwierząt.' WHERE id = 451;

UPDATE plants SET notes = 'Najpopularniejszy rozchodnik. Liście sukulentowe. Wyjątkowo odporny na suszę. Kwitnie od września do października, kwiaty zmieniają barwę z różowej na brązową. Nie dzielić często. Przyciąga motyle. Pozostawić na zimę – ozdobny. Nie podlewać.' WHERE id = 452;

UPDATE plants SET notes = 'Purpurowe łodygi i liście, różowe kwiaty. Liście sukulentowe. Bardzo odporny na suszę. Przyciąga motyle. Pozostawić na zimę.' WHERE id = 453;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 2015. Wyjątkowo długi okres kwitnienia od czerwca do października. Niebieskie kwiaty z białym środkiem. Bardzo tolerancyjna, nie wymaga przycinania. Rozrasta się do 120 cm średnicy. Nie rozmnaża się z samosiewu (sterylna).' WHERE id = 454;

UPDATE plants SET notes = 'Klasyczna niebieska odmiana bodziszka. Przycięcie po kwitnieniu stymuluje drugą falę w sierpniu. Bardzo tolerancyjna. Dzielić co 4-5 lat.' WHERE id = 455;

UPDATE plants SET notes = 'Czerwono-pomarańczowe kwiaty blaknące do barwy brzoskwiniowej. Aromatyczne liście. Bardzo odporny na suszę. Roślina ekspansywna – dzielić co 2-3 lata. Przycięcie po kwitnieniu stymuluje drugą falę. Kwiaty nadają się do suszenia.' WHERE id = 456;

UPDATE plants SET notes = 'Cytrynowożółte kwiaty, srebrzyste liście. Bardzo odporny na suszę. Przycinać po kwitnieniu. Roślina ekspansywna – dzielić co 2-3 lata. Kwiaty nadają się do suszenia.' WHERE id = 457;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 1997. Ciemnofioletowe kwiaty. Przycięcie po pierwszym kwitnieniu stymuluje drugą falę we wrześniu. Aromatyczne liście. Bardzo tolerancyjna. Dzielić co 3-4 lata. Przyciąga pszczoły.' WHERE id = 458;

UPDATE plants SET notes = 'Ciemnopurpurowe kwiaty na czarnych łodygach. Bardzo efektowna odmiana. Przycinać po kwitnieniu. Tolerancyjna. Przyciąga pszczoły.' WHERE id = 459;

UPDATE plants SET notes = 'Klasyczny gatunek dzwonka ogrodowego. Kwiaty niebieskie lub białe. Przycinanie po kwitnieniu przedłuża okres dekoracyjności. Rozmnaża się z samosiewu. Chronić przed ślimakami. Dzielić co 3-4 lata.' WHERE id = 460;

UPDATE plants SET notes = 'Kompaktowy gatunek dzwonka. Obficie kwitnący. Przycinać po kwitnieniu. Tolerancyjny. Dzielić co 3-4 lata. Idealny do ogrodów skalnych.' WHERE id = 461;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 1992. Jasnożółte, drobne kwiaty. Wyjątkowo długi okres kwitnienia od czerwca do września. Bardzo odporny na suszę. Delikatne, nitkowate liście. Dzielić co 3 lata. Przycinanie w sierpniu wspomaga krzewienie.' WHERE id = 462;

UPDATE plants SET notes = 'Duże żółte kwiaty o średnicy 5-7 cm. Odporny na suszę. Roślina krótko żyjąca (2-3 lata), ale rozmnaża się z samosiewu. Przycinać po kwitnieniu. Nie przenawozić.' WHERE id = 463;

UPDATE plants SET notes = 'Ciemnobordowe kwiaty. Wyjątkowo długi okres kwitnienia od czerwca do października. Bardzo odporny na suszę. Przycinać po kwitnieniu. Roślina krótko żyjąca (2-4 lata), ale rozmnaża się z samosiewu. Nie przenawozić. Nie znosi podmokłego podłoża zimą.' WHERE id = 464;

UPDATE plants SET notes = 'Kompaktowa odmiana o nietypowych rurkowych płatkach. Żółto-czerwone kwiaty. Długi okres kwitnienia. Bardzo odporny na suszę. Nie znosi wilgotnej zimy.' WHERE id = 465;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 1991. Purpurowe liście ozdobne przez cały sezon, kwiaty mniej istotne. Młode rośliny okrywać zimą. Dzielić co 3-4 lata. Wiosną przysypać odsłonięte korzenie.' WHERE id = 466;

UPDATE plants SET notes = 'Limonkowożółte liście stanowiące jasny akcent w zacienionych miejscach. Roślina kompaktowa. Dzielić co 3-4 lata. Okrywać zimą.' WHERE id = 467;

UPDATE plants SET notes = 'Białe kwiatostany niczym welon panny młodej. Bezwzględnie wymaga wilgotnej gleby – sadzić przy wodzie lub obficie podlewać. Stanowisko cieniste lub półcieniste. Ściółkować. Dzielić co 4-5 lat. Pozostawić na zimę – ozdobna.' WHERE id = 468;

UPDATE plants SET notes = 'Intensywnie czerwone kwiatostany. Brązowozielone liście. Bezwzględnie wymaga wilgotnej gleby. Obficie podlewać. Ściółkować. Dzielić co 4-5 lat.' WHERE id = 469;

UPDATE plants SET notes = 'Zimozielona roślina o dużych liściach. Wczesne kwitnienie w kwietniu-maju. Niezwykle tolerancyjna – rośnie w słońcu i cieniu, na glebach suchych i wilgotnych. Liście czerwienieją zimą. Pokrywa grunt. Dzielić co 5 lat lub rzadziej.' WHERE id = 470;

UPDATE plants SET notes = 'Najpopularniejszy miniaturowy narcyz. Na jednej łodydze 2-3 kwiaty. Cebule można zostawiać w ziemi – naturalizują się. Doskonały do pojemników. Nie ścinać liści po kwitnieniu – muszą uschnąć naturalnie.' WHERE id = 471;

UPDATE plants SET notes = 'Duże kwiaty o średnicy 10-11 cm. Biały z żółtą koroną blaknącą do białej. Bardzo plenny. Zostawiać w ziemi – naturalizuje się. Nie ścinać liści.' WHERE id = 472;

UPDATE plants SET notes = 'Żółte, odgięte płatki i pomarańczowa korona. Odmiana wczesna, bardzo plenna. Doskonale naturalizuje się. Zostawiać w ziemi. Pokrój kompaktowy.' WHERE id = 473;

UPDATE plants SET notes = 'Intensywny zapach. Ciemnoróżowe, gęste kwiatostany. Cebule można zostawiać w ziemi, choć słabną po 2-3 latach. Doskonały do pojemników i pędzenia. Nie ścinać liści.' WHERE id = 474;

UPDATE plants SET notes = 'Czysto białe kwiaty o intensywnym zapachu. Gęste kwiatostany. Doskonały do pędzenia. Można zostawiać w ziemi. Klasyczna odmiana.' WHERE id = 475;

UPDATE plants SET notes = 'Odmiana o pełnych kwiatach, nietypowa dla szafirka. Bardzo ekspansywna – szybko się rozrasta. Zostawiać w ziemi. Rozmnaża się z samosiewu. Sadzić grupowo (50-100 sztuk). Nie ścinać liści. Liście pojawiają się jesienią.' WHERE id = 476;

UPDATE plants SET notes = 'Pierwsza roślina kwitnąca wiosną – pojawia się w lutym-marcu, nawet w śniegu. Zostawiać w ziemi – naturalizuje się. Ekspansywny. Sadzić grupowo (ponad 100 sztuk). Dzielić po kwitnieniu, gdy liście są jeszcze zielone. Nie ścinać liści.' WHERE id = 477;

UPDATE plants SET notes = 'Odmiana pręgowana biało-fioletowa. Wczesna wiosenna roślina cebulowa. Zostawiać w ziemi – naturalizuje się. Sadzić grupowo. Liście muszą uschnąć po kwitnieniu. Ważne źródło pokarmu dla pszczół wczesną wiosną.' WHERE id = 478;

UPDATE plants SET notes = 'Ciemnobordowa odmiana liliowa, niemal czarna. Lilia azjatycka – brak zapachu. Kwiaty skierowane do góry. Ściółkować. Można zostawiać w ziemi (mrozoodporna). Przesadzać co 3-4 lata.' WHERE id = 479;

UPDATE plants SET notes = 'Kompaktowa lilia idealna do pojemników. Pomarańczowe kwiaty. Brak zapachu. Można zostawiać w ziemi. Przesadzać co 3-4 lata.' WHERE id = 480;

UPDATE plants SET notes = 'Oryginalna odmiana z hodowli Floret. Tęczowy efekt ombre z lawendowymi środkami kwiatów. Regularne cięcie stymuluje ciągłe kwitnienie. Podlewać u podstawy rośliny, unikając liści – zapobiega mączniakowi. Roślina ciepłolubna – wysiew w maju po mrozach.' WHERE id = 481;

UPDATE plants SET notes = 'Ogromne kwiaty o średnicy 12-15 cm. Długie łodygi (ponad 100 cm) – doskonała do bukietów. Regularne cięcie stymuluje ciągłe kwitnienie. Podlewać u podstawy. Wysiew w maju po mrozach lub rozsada w kwietniu.' WHERE id = 482;

UPDATE plants SET notes = 'Kompaktowa odmiana odporna na mączniaka. Obficie kwitnąca. Samoczyszcząca – nie wymaga usuwania zwiędłych kwiatów. Tolerancyjna. Doskonała na rabaty.' WHERE id = 483;

UPDATE plants SET notes = 'Aksamitny słonecznik w barwach bordowo-brązowych. Wielokwiatowy – 5-10 kwiatów na roślinie. Regularne cięcie stymuluje kwitnienie. Doskonały do bukietów. Wysiew w kwietniu-maju bezpośrednio do gruntu. Nasiona stanowią pokarm dla ptaków.' WHERE id = 484;

UPDATE plants SET notes = 'Karłowy słonecznik o pełnych kwiatach przypominających pompony. Kompaktowy – idealny do pojemników. Wysiew w kwietniu-maju. Bardzo lubiany przez dzieci.' WHERE id = 485;

UPDATE plants SET notes = 'Nietypowa biała odmiana aksamitki. Duże kwiaty o średnicy 7-10 cm. Odpędza szkodniki z ogrodu (nicienie, mszyce). Wysiew na rozsadę w kwietniu lub bezpośrednio do gruntu w maju. Usuwać przekwitnięte kwiaty.' WHERE id = 486;

UPDATE plants SET notes = 'Kompaktowa, ciemnoczerwona odmiana. Odpędza szkodniki w warzywniaku. Bardzo tolerancyjna – rośnie nawet na ubogiej glebie. Wysiew bezpośrednio do gruntu w maju lub rozsada w kwietniu. Rozmnaża się z samosiewu.' WHERE id = 487;

UPDATE plants SET notes = 'Intensywnie pachnąca odmiana. Roślina pnąca wymagająca podpór (siatka, tyczki). Regularne cięcie kwiatów przedłuża okres kwitnienia. Nie znosi upałów powyżej 25°C. Wysiew w marcu-kwietniu – znosi przymrozki do -5°C. Optymalne pH 7,0-7,5. Ściółkować.' WHERE id = 488;

UPDATE plants SET notes = 'Idealna do miejsc zacienionych i półcienistych. Pełne, pomarańczowe kwiaty. Obficie kwitnąca. Bulwy wykopać w październiku przed mrozami. Przechowywać w torfie w chłodnym miejscu (5-10°C). Nie znosi pełnego słońca.' WHERE id = 489;

UPDATE plants SET notes = 'Polska odmiana sałaty masłowej o delikatnych, maślanych liściach. Odporna na pękanie. Wysiew sukcesyjny od marca do sierpnia co 2 tygodnie. Roślina chłodolubna – nie znosi temperatur powyżej 20°C. W upale szybko kwitnie. Obficie podlewać.' WHERE id = 490;

UPDATE plants SET notes = 'Klasyczna sałata lodowa o zwartych główkach i chrupiących liściach. Powolny wzrost. Odporna na pękanie i przedwczesne kwitnienie. Wysiew w kwietniu-maju. Roślina chłodolubna. Wymaga bardzo dużych ilości wody. W lodówce przechowuje się do 2 tygodni.' WHERE id = 491;

UPDATE plants SET notes = 'Elegancka trawa ozdobna o wąskich liściach. Srebrzyste wiechy pojawiają się we wrześniu-październiku. Przycinać wiosną w marcu na wysokość 5-10 cm. Dzielić rzadko (co 5 lat lub rzadziej). Pozostawić na zimę – ozdobna. Liście tworzą efekt fontanny.' WHERE id = 492;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 2009. Białe obrzeżenie liści tworzy efekt podświetlenia. Pokrój kompaktowy. Przycinać wiosną w marcu. Pozostawić na zimę.' WHERE id = 493;

UPDATE plants SET notes = 'Odmiana nagrodzona tytułem Byliny Roku 2001. Pionowa, sztywna forma. Wczesne kwitnienie w czerwcu-lipcu. Bardzo tolerancyjna. Nie rozmnaża się z samosiewu (sterylna). Przycinać wiosną w marcu.' WHERE id = 494;

UPDATE plants SET notes = 'Trawa o niebieskim zabarwieniu liści. Kompaktowa kępa. Zimozielona. Bardzo odporna na suszę. Nie nawozić. Dzielić co 2-3 lata dla odmłodzenia. Przycinać wczesną wiosną.' WHERE id = 495;

UPDATE plants SET notes = 'Liście z biało-zielonymi paskami. Bardzo ekspansywna – rozprzestrzenia się rozłogami. Sadzić z barierą korzeniową lub w pojemniku. Toleruje mokrą glebę, idealna przy oczkach wodnych. Przycinanie w lipcu powoduje odrost świeżych liści.' WHERE id = 496;

UPDATE plants SET notes = 'Najpopularniejsza paproć europejska. Zimozielona w łagodnych zimach. Bardzo tolerancyjna – rośnie nawet w głębokim cieniu. Ściółkować liśćmi. Nie dzielić często.' WHERE id = 497;

UPDATE plants SET notes = 'Efektowna, wysoka paproć. Rozprzestrzenia się rozłogami – ekspansywna. Wymaga wilgotnego podłoża – idealna przy oczku wodnym. Ściółkować. Młode, zwinięte pędy są jadalne.' WHERE id = 498;

UPDATE plants SET notes = 'Krzew, nie bylina. Ogromne kwiaty o średnicy 20-25 cm. Roślina długowieczna (ponad 50 lat). Nie przesadzać. Ciężkie kwiaty wymagają podpór. Wolny wzrost przez pierwsze lata. Chronić przed wiosennymi przymrozkami.' WHERE id = 499;

UPDATE plants SET notes = 'Fioletowa kopuła – odmiana o kompaktowym, kulistym pokroju. Masowe kwitnienie od sierpnia do października, gdy inne byliny już przekwitły. Odporna na mączniaka, który stanowi problem starszych odmian. Dzielić co 2-3 lata. Przycinanie w czerwcu o jedną trzecią daje gęstszy, niższy pokrój. Przyciąga motyle.' WHERE id = 504;

UPDATE plants SET notes = 'Ogniste kwiaty w barwach czerwono-żółtych jak zachód słońca. Usuwanie przekwitniętych kwiatów zapewnia kwitnienie przez cały sezon. Toleruje suszę, upały i ubogą glebę. Roślina krótko żyjąca (2-3 lata), ale rozmnaża się z samosiewu. Kompaktowa – idealna na rabaty. Odmiana nagrodzona w konkursie All-America Selections.' WHERE id = 506;

UPDATE plants SET notes = 'Zimozielone, duże, skórzaste liście o długości 20-30 cm, zimą przybierające purpurowoczerwoną barwę. Wczesne kwitnienie w kwietniu-maju. Rośnie praktycznie w każdych warunkach – w słońcu i cieniu, na glebie suchej i wilgotnej. Pokrywa grunt. Dzielić co 4-5 lat. Niezniszczalna roślina, zimą bardzo dekoracyjna.' WHERE id = 509;

UPDATE plants SET notes = 'Agresywnie pokrywa grunt. Ciemnofioletowe liście i niebieskie, świeczkowate kwiatostany. Rozmnaża się rozłogami – szybko tworzy zwarty dywan. Zimozielona. Kontrolować ekspansję. Idealna pod drzewa i na skarpy. Skutecznie wypiera chwasty.' WHERE id = 510;

UPDATE plants SET notes = 'Wczesna wiosna przynosi kwiaty i piękne liście przez cały sezon. Kwiaty zmieniają barwę z różowej na niebieską – jedna roślina ma dwa kolory. Wąskie, intensywnie srebrzyste liście. Pokrywa grunt w zacienionych miejscach. Dzielić co 3-4 lata. Przycięcie po kwitnieniu powoduje odrost świeżych liści. Pierwsze kwiaty już w marcu.' WHERE id = 512;

UPDATE plants SET notes = 'Srebrne liście jak pokryte szronem. Duże, sercowate liście, srebrzyste z zielonymi żyłkami. Wiosną chmura drobnych niebieskich kwiatów. Pokrywa grunt w zacienionych miejscach. Toleruje suszę (w cieniu). Odmiana nagrodzona tytułem Byliny Roku 2012. Spektakularny wygląd przez cały sezon.' WHERE id = 513;

UPDATE plants SET notes = 'Delikatne, piankowe kwiatostany. Kłosowate różowe kwiaty nad ozdobnymi liśćmi. Liście karbowane z ciemnymi żyłkami, zimozielone. Wolno rosnąca roślina okrywowa. Idealna pod rododendrony (preferuje kwaśną glebę). Wymaga cienia. Dzielić co 3-4 lata.' WHERE id = 514;

UPDATE plants SET notes = 'Pióropusze kolorów w zacienionych miejscach. Ciemnoczerwone, pierzaste kwiatostany. Młode liście brązowozielone. Bezwzględnie wymaga wilgoci – przy niedoborze wody traci walory dekoracyjne. Ściółkować. Dzielić co 3-4 lata wiosną. Kwiaty pozostają ozdobne również po uschnięciu. Idealna przy stawie.' WHERE id = 516;

UPDATE plants SET notes = 'Biała elegancja jesieni. Duże (6-8 cm) pojedyncze białe kwiaty na wysokich łodygach. Kwitnienie od sierpnia do października – gdy inne byliny już przekwitły. Rozrasta się kłączami – może być ekspansywna. Wolny start przez pierwsze dwa lata, potem szybka ekspansja. Odmiana uprawiana od 1858 roku, wyróżniona nagrodą Award of Garden Merit.' WHERE id = 517;

UPDATE plants SET notes = 'Magnes dla pszczół i motyli. Intensywnie czerwone, kudłate kwiaty. Aromatyczne liście używane do przyrządzania herbaty bergamotowej. Problem: podatność na mączniaka – sadzić w przewiewnych miejscach. Dzielenie co 2-3 lata zapobiega mączniakowi. Rozrasta się kłączami.' WHERE id = 518;

UPDATE plants SET notes = 'Kule fioletowych dzwonków. Kwiaty skupione w głowiastych kwiatostanach. Przycięcie po kwitnieniu może spowodować powtórne kwitnienie. Rozrasta się kłączami – może być ekspansywna. Dzielić co 2-3 lata. Łatwo naturalizuje się w ogrodzie.' WHERE id = 520;

UPDATE plants SET notes = 'Różowa czarownica o zapachu goździka. Intensywnie różowe kwiaty o silnym aromacie. Sinoszare liście, zimozielone. Usuwanie przekwitniętych stymuluje ponowne kwitnienie. Toleruje suszę. Odmiana nagrodzona tytułem Byliny Roku 2006. Pokrój kompaktowy.' WHERE id = 521;

UPDATE plants SET notes = 'Klasyczna margerytka w powiększonej wersji. Duże białe kwiaty o średnicy 8-10 cm na mocnych łodygach niewymagających podpór. Długi okres kwitnienia. Usuwać przekwitnięte kwiaty. Dzielić co 2-3 lata wiosną. Odmiana nagrodzona tytułem Byliny Roku 2003. Niezawodna bylina.' WHERE id = 522;

UPDATE plants SET notes = 'Królewskie świece kwiatów. Długie (30 cm), pionowe kłosy niebieskich kwiatów. Kwitnienie trwa wiele tygodni. Mocne łodygi niewymagające podpór. Usuwanie przekwitniętych kłosów stymuluje boczne kwiaty. Dzielić co 3-4 lata. Przyciąga bardzo dużo pszczół.' WHERE id = 523;

UPDATE plants SET notes = 'Puchate świece kwiatów. Kwiaty na kłosach kwitną od góry – cecha unikalna wśród bylin. Odmiana kompaktowa. Bardzo przyciąga motyle. Bulwy zimują w ziemi. W miejscach wietrznych podpierać. Dzielić co 3-4 lata. Doskonała do wazonu.' WHERE id = 524;

UPDATE plants SET notes = 'Dwukolorowe pochodnie w barwie papai. Kwiaty pomarańczowe u góry, żółte u dołu. Odmiana kompaktowa, idealna do małych ogrodów. Liście zimozielone. Bulwy pozostawiać w gruncie z zimowym okryciem w chłodniejszych regionach. Przyciąga kolibry, pszczoły i motyle.' WHERE id = 525;
