-- Naprawa opisów roślin - Batch 3 (ID 539-638)
-- Profesjonalny, spójny styl polski

UPDATE plants SET notes = 'Zwiewna bylina o cienkich łodygach 100-150 cm, zakończonych fioletowymi kwiatostanami. Efekt przezroczystości – roślina nie zasłania widoku. Obficie samosiewna. Przyciąga motyle i pszczoły. Kwitnie do pierwszych mrozów. Nagroda RHS Award of Garden Merit.' WHERE id = 539;

UPDATE plants SET notes = 'Bylina okrywowa o płożących pędach rozrastających się na 60-90 cm. Purpurowe kwiaty przez cały sezon. Bardzo odporna na suszę. Perennial of the Year 1995. Idealna na suche, słoneczne stanowiska.' WHERE id = 540;

UPDATE plants SET notes = 'Kompaktowa odmiana o sztywnych łodygach i lawendowych kwiatostanach. Długi okres kwitnienia. Rozmnaża się z samosiewu. Przyciąga motyle. Toleruje suszę. W Polsce wymaga osłony zimowej w chłodniejszych regionach.' WHERE id = 541;

UPDATE plants SET notes = 'Klasyczna odmiana o białych, drobnych, pełnych kwiatach tworzących zwiewne wiechy. Głęboki system korzeniowy – nie przesadzać po posadzeniu. Preferuje gleby wapienne. Przycięcie po kwitnieniu stymuluje drugą falę. Doskonała roślina do bukietów.' WHERE id = 542;

UPDATE plants SET notes = 'Niska, płożąca odmiana do ogrodów skalnych. Różowe kwiaty, pędy rozrastają się na 40-60 cm. Bardzo odporna na suszę. Preferuje gleby wapienne. Przycinać po kwitnieniu.' WHERE id = 543;

UPDATE plants SET notes = 'Odmiana o różowych, pełnych kwiatach w obfitych wiechach tworzących zwiewny efekt. Przycięcie po pierwszym kwitnieniu stymuluje powtórne kwitnienie. Preferuje gleby wapienne. Długowieczna bylina.' WHERE id = 544;

UPDATE plants SET notes = 'Wczesna bylina kwitnąca wiosną. Purpurowe kwiaty z żółtym oczkiem, ciemnozielone liście. Wymaga wilgotnego i zacieniomego stanowiska. Dzielić co 2-3 lata. Bardzo mrozoodporna.' WHERE id = 545;

UPDATE plants SET notes = 'Rodzimy gatunek chroniony o żółtych, aromatycznych kwiatach na łodygach 20-30 cm. Doskonale naturalizuje się i rozmnaża z samosiewu. Preferuje wilgotne, cieniste stanowiska.' WHERE id = 546;

UPDATE plants SET notes = 'Alpejski gatunek o wielobarwnych kwiatach pokrytych charakterystycznym nalotem. Mięsiste liście. Wymaga gleby wapiennej z dobrym drenażem. Roślina kolekcjonerska o wiktoriańskim rodowodzie.' WHERE id = 547;

UPDATE plants SET notes = 'Hosta Roku 2001. Liście niebiesko-zielone ze złoto-żółtym centrum – barwa zmienia się przez sezon. Odmiana kompaktowa, odporna na ślimaki. Efektowna przez cały sezon wegetacyjny.' WHERE id = 548;

UPDATE plants SET notes = 'Hosta Roku 2008. Miniaturowa odmiana o małych, okrągłych liściach koloru niebiesko-zielonego przypominających uszy myszy. Idealna do małych ogrodów i pojemników. Grube liście odporne na ślimaki.' WHERE id = 549;

UPDATE plants SET notes = 'Odmiana o liściach zmieniających barwę przez sezon – od zielonych po złotożółte z zielonym brzegiem. Szybko rośnie i tworzy efektowne kępy.' WHERE id = 550;

UPDATE plants SET notes = 'Piwonia o japońskiej formie kwiatu – różowe płatki zewnętrzne otaczają kremowe centrum. Kwiaty 15-18 cm, aromatyczne. Roślina długowieczna (ponad 50 lat). Nie przesadzać – pełnia kwitnienia w 2-3 roku. Nagroda RHS Award of Garden Merit.' WHERE id = 551;

UPDATE plants SET notes = 'Wyjątkowa odmiana o kwiatach zmieniających barwę – od koralowej przez brzoskwiniową do kremowej. Kwiaty półpełne, 18-20 cm. Złoty Medal APS 1986. Odmiana wczesna o cudownym zapachu.' WHERE id = 552;

UPDATE plants SET notes = 'Klasyczna odmiana z 1908 roku o ciemnoczerwonych, pełnych kwiatach 15-18 cm. Intensywny zapach. Silny wzrost, mocne łodygi nie wymagające podpór.' WHERE id = 553;

UPDATE plants SET notes = 'Klasyczna odmiana z 1932 roku o ciemnoniebieskich, aksamitnych kwiatach 8-10 cm. Preferuje wilgotne stanowiska – idealny nad wodą. Mniej wymagający niż irys bródkowy. Dzielić co 3-4 lata. Nagroda RHS Award of Garden Merit.' WHERE id = 554;

UPDATE plants SET notes = 'Efektowna odmiana o białych kwiatach z fioletową lamówką, falistych płatkach 12-15 cm. Medal Dysona 1977. Aromatyczny. Kłącza sadzić płytko – lubią słońce. Dzielić co 3-5 lat.' WHERE id = 555;

UPDATE plants SET notes = 'Unikalna odmiana o fioletowych kwiatach z białymi przebarwieniami – każdy kwiat jest niepowtarzalny. Efekt batiku. Często kwitnie powtórnie jesienią. Nagroda Award of Merit.' WHERE id = 556;

UPDATE plants SET notes = 'Odmiana o jasnopomarańczowych kwiatach 12-14 cm z czerwonym gardłem. Kwitnie powtórnie przez sezon. Roślina kompaktowa. Usuwać zwiędłe kwiaty dla przedłużenia kwitnienia.' WHERE id = 557;

UPDATE plants SET notes = 'Odmiana powstała jako mutacja od Stella de Oro. Żółte kwiaty z ciemnoczerwonym oczkiem. Kwitnienie przez cały sezon. Bardzo odporna i łatwa w uprawie.' WHERE id = 558;

UPDATE plants SET notes = 'Odmiana o pająkowatej formie kwiatów – wąskie, rubinowo-czerwone płatki z żółtym gardłem. Kwiaty 18 cm. Medal Stantona 1998. Egzotyczny wygląd.' WHERE id = 559;

UPDATE plants SET notes = 'Uznawana za najlepszą żółtą różę Davida Austina. Kwiaty złotożółte, pełne, rozetowe, 10-12 cm. Intensywny zapach herbaty. Kwitnienie powtarzające. Może rosnąć jako pnącze. Ulubiona Róża Świata 2000.' WHERE id = 560;

UPDATE plants SET notes = 'Uznawana za najlepiej pachnącą różę Davida Austina – intensywny, klasyczny zapach starej róży. Różowe kwiaty rozetowe 10-12 cm. Kwitnienie powtarzające. Olejek wykorzystywany w perfumiarstwie. Nagroda RHS Award of Garden Merit.' WHERE id = 561;

UPDATE plants SET notes = 'Wszechstronna odmiana rosnąca jako krzew (150 cm) lub pnącze (300 cm). Delikatne bladoróżowe kwiaty pełne 10 cm. Intensywny zapach muszkateli. Kwitnienie powtarzające. Bardzo odporna. Nagroda RHS Award of Garden Merit.' WHERE id = 562;

UPDATE plants SET notes = 'Odmiana o stożkowych kwiatostanach 25-30 cm zmieniających barwę przez sezon – od limonkowozielonej przez kremową do różowej jesienią. Ciąć wiosną mocno – kwitnie na pędach tegorocznych. Suszone kwiaty ozdobne zimą. Nagroda RHS Award of Garden Merit.' WHERE id = 563;

UPDATE plants SET notes = 'Odmiana o imponujących kwiatostanach 30-40 cm tworzących gradient kolorów – od białego przez różowy do bordowego. Ciąć wiosną. Nagroda RHS Award of Garden Merit. Jedna z najpiękniejszych hortensji bukietowych.' WHERE id = 564;

UPDATE plants SET notes = 'Przełomowa odmiana kwitnąca zarówno na starym, jak i nowym drewnie – kwitnienie przez cały sezon. Kwiatostany kuliste 15-20 cm. Kolor zależy od pH gleby: kwaśne – niebieski, zasadowe – różowy. Nie ciąć mocno.' WHERE id = 565;

UPDATE plants SET notes = 'Samoczepiająca się pnącze o przyssawkach pozwalających wspinać się bez podpór. Białe, aromatyczne kwiatostany 15-25 cm. Powolny start (3-5 lat), następnie szybki wzrost. Toleruje cień i północne ekspozycje. Żółte liście jesienią. Nagroda RHS Award of Garden Merit.' WHERE id = 566;

UPDATE plants SET notes = 'Zimozielona azalia o purpurowofuksji kwiatach 5-6 cm. Pokrój kompaktowy. Bezwzględnie wymaga kwaśnej gleby (pH 4,5-5,5). Ściółkować korą. Regularnie podlewać.' WHERE id = 567;

UPDATE plants SET notes = 'Bardzo mrozoodporna azalia (-29°C) o ceglastoczerwonych kwiatach. Zimozielone liście przebarwiają się na bordowo zimą. Efektowne kwitnienie. Odmiana nagradzana.' WHERE id = 568;

UPDATE plants SET notes = 'Azalia o nietypowych, pełnych kwiatach przypominających miniaturowe różyczki. Różowa barwa. Zimozielona, kompaktowa, mrozoodporna. Długi okres kwitnienia.' WHERE id = 569;

UPDATE plants SET notes = 'Odmiana o kwiatach zmieniających barwę w zależności od temperatury: przy chłodnej pogodzie czerwono-białe, przy ciepłej czerwone, w upał białe. Aromatyczne liście (owocowy zapach). Długie kwitnienie. Przyciąga pszczoły. Nagroda RHS Award of Garden Merit.' WHERE id = 570;

UPDATE plants SET notes = 'Odmiana o ciemnofioletowych kwiatach z niemal czarnymi pąkami. Długie kwitnienie do mrozów. Wysoka, krzaczasta. Przyciąga pszczoły. Nagroda RHS Award of Garden Merit.' WHERE id = 571;

UPDATE plants SET notes = 'Odmiana o granatowych kwiatostanach 15-20 cm. Długi okres kwitnienia. Toleruje suszę. W Polsce uprawiana jako roślina jednoroczna, może przetrwać łagodne zimy. Nagroda RHS Award of Garden Merit.' WHERE id = 572;

UPDATE plants SET notes = 'Kompaktowa odmiana o ciemnofioletowych kwiatach i intensywnym zapachu. Bardzo mrozoodporna. Ciąć po kwitnieniu i wiosną dla odmłodzenia. Nie ciąć starego, zdrewniałego przyrostu. Nagroda RHS Award of Garden Merit. Najlepsza odmiana do chłodniejszych klimatów.' WHERE id = 573;

UPDATE plants SET notes = 'Odmiana kwitnąca 2-3 tygodnie wcześniej niż Hidcote. Jasnofioletowe kwiaty. Kompaktowa, mrozoodporna. Ciąć po kwitnieniu. Ulubiona odmiana Gertrude Jekyll. Nagroda RHS Award of Garden Merit.' WHERE id = 574;

UPDATE plants SET notes = 'Wysoka odmiana (180 cm) o granatowoczarnych kwiatach 5 cm. Aromatyczne liście o zapachu anyżu. Przyciąga pszczoły. Może zimować w cieplejszych regionach przy grubym ściółkowaniu. Nagroda RHS Award of Garden Merit.' WHERE id = 575;

UPDATE plants SET notes = 'Kompaktowa odmiana (30-40 cm) o lawendowych kwiatach, bardzo obficie kwitnąca. Idealna na przód rabat. Mniej podatna na mączniaka prawdziwego niż inne astry. Dzielić co 2-3 lata. Nagroda RHS Award of Garden Merit. Klasyka jesiennych rabat.' WHERE id = 576;

UPDATE plants SET notes = 'Odmiana o intensywnie karminowo-różowych kwiatach, bardzo obfita. Odporna na mączniaka prawdziwego. Przycięcie w czerwcu powoduje kompaktowy wzrost. Nagroda RHS Award of Garden Merit. Klasyka jesiennych rabat.' WHERE id = 577;

UPDATE plants SET notes = 'Odmiana tworząca chmurę drobnych, lawendowo-niebieskich kwiatów. Toleruje suszę. Nie wymaga dzielenia. Samooczyszczająca – zwiędłe kwiaty opadają same. Bardzo łatwa w uprawie. Nagroda RHS Award of Garden Merit.' WHERE id = 578;

UPDATE plants SET notes = 'Odmiana o ciemnoczerwonych, półpełnych kwiatach 5-6 cm. Aromatyczne. Przycięcie w czerwcu powoduje kompaktowy wzrost. Mrozoodporna. Dzielić co 2-3 lata.' WHERE id = 579;

UPDATE plants SET notes = 'Odmiana o różowopurpurowych, płaskich kwiatostanach 10-15 cm przyciągających motyle. Mięsiste, sukulentowe liście. Toleruje suszę. Suszone kwiaty ozdobne zimą. Dzielić co 3-4 lata. Nagroda RHS Award of Garden Merit.' WHERE id = 580;

UPDATE plants SET notes = 'Odmiana okrywowa o żółtych, gwiazdkowatych kwiatach. Rozrasta się na 40-60 cm. Toleruje suszę i ubogą glebę. Mięsiste liście. Idealna na suche, trudne stanowiska. Nagroda RHS Award of Garden Merit.' WHERE id = 581;

UPDATE plants SET notes = 'Kompaktowa odmiana okrywowa o purpurowo-różowych kwiatach 3-4 cm. Długi okres kwitnienia. Liście czerwienieją jesienią. Rozrasta się na 45-60 cm. Toleruje suszę. Gatunek otrzymał tytuł Perennial Plant of the Year 2001.' WHERE id = 582;

UPDATE plants SET notes = 'Sadzić od kwietnia do maja co 2 tygodnie dla przedłużenia okresu kwitnienia. Wysokie odmiany wymagają podpór. Wykopywać w październiku przed mrozami. Przechowywać cebule w chłodzie (5-10°C) w torfie. W Polsce nie zimują w gruncie.' WHERE id = 583;

UPDATE plants SET notes = 'Rodzimy gatunek leśny doskonale naturalizujący się pod drzewami. Białe, gwiaździste kwiaty. Rozłogi tworzą zwarte pokrywy. Cebule pozostawiać w gruncie. Ściółkować liśćmi.' WHERE id = 584;

UPDATE plants SET notes = 'Miniaturowy narcyz o intensywnym zapachu. Łodyga nosi 2-4 kwiaty. Doskonale naturalizuje się. Bardzo tolerancyjny – nadaje się do skalniaków. Pozostawiać w gruncie.' WHERE id = 585;

UPDATE plants SET notes = 'Efektowna cebulowa o purpurowych, kulistych kwiatostanach 10 cm. Pozostawiać w gruncie – naturalizuje się. Sadzić grupowo (25-50 sztuk). Liście usychają przed kwitnieniem – sadzić wśród bylin.' WHERE id = 586;

UPDATE plants SET notes = 'Odmiana o imponujących kulach kwiatowych 15-20 cm. Długi okres kwitnienia (3-4 tygodnie). Pozostawiać w gruncie. Doskonały do suszenia – zachowuje barwę. Odmiana nagradzana.' WHERE id = 587;

UPDATE plants SET notes = 'Odmiana kwitnąca jesienią bez liści. Pełne, różowe kwiaty. Liście pojawiają się wiosną – są duże i mogą zakrywać inne rośliny. Roślina silnie trująca. Sadzić w lipcu-sierpniu. Pozostawiać w gruncie.' WHERE id = 588;

UPDATE plants SET notes = 'Miniaturowa odmiana botaniczna o różowo-fioletowych kwiatach z niebieskim centrum. Wczesne kwitnienie (marzec-kwiecień). Naturalizuje się – wraca co rok. Idealny do skalniaków. Nie wykopywać.' WHERE id = 589;

UPDATE plants SET notes = 'Efektowna odmiana papuzia o czerwono-białych, postrzępionych płatkach. Kwitnienie późne (maj). Bardzo dekoracyjna. Wykopywać po przekwitnięciu lub traktować jako jednoroczną.' WHERE id = 590;

UPDATE plants SET notes = 'Odmiana o żółto-pomarańczowych płatkach z frędzlowanymi brzegami. Długi okres kwitnienia. Efektowna w grupach.' WHERE id = 591;

UPDATE plants SET notes = 'Klasyczna odmiana hybrydowa o dużych, żółtych kwiatach. Silny wzrost. Hybrydy Darwina wracają przez 3-5 lat. Odporna na wiatr.' WHERE id = 592;

UPDATE plants SET notes = 'Elegancka odmiana o pomarańczowych kwiatach ze szpiczastymi płatkami. Delikatny aromat. Kwitnienie późne. Delikatny, wytworny wygląd.' WHERE id = 593;

UPDATE plants SET notes = 'Klasyczna odmiana o czystożółtych kwiatach i mocnych łodygach. Kwitnienie w środku sezonu. Doskonała do cięcia.' WHERE id = 594;

UPDATE plants SET notes = 'Odmiana o intensywnie purpurowych kwiatach. Kwitnienie w środku sezonu. Doskonała do cięcia. Efektowna w grupach.' WHERE id = 595;

UPDATE plants SET notes = 'Odmiana o pełnych, różowo-kremowych kwiatach przypominających piwonie. Aromatyczna. Kwitnienie późne (maj). Jedna z najpopularniejszych odmian.' WHERE id = 596;

UPDATE plants SET notes = 'Wczesna odmiana o różowo-kremowych płatkach z czerwonymi akcentami. Kwiaty otwierają się płasko w kształcie gwiazdy. Naturalizuje się. Marmurowe liście. Nie wykopywać.' WHERE id = 597;

UPDATE plants SET notes = 'Odmiana o szkarłatnych kwiatach i ozdobnych, marmurowych liściach z purpurowymi paskami. Naturalizuje się. Ozdobna przez cały sezon dzięki liściom. Nie wykopywać.' WHERE id = 598;

UPDATE plants SET notes = 'Klasyczna odmiana o dużych, żółtych kwiatach z długą trąbką. Aromatyczna. Doskonale naturalizuje się. Nie wykopywać – wraca przez wiele lat. Nagroda RHS Award of Garden Merit.' WHERE id = 599;

UPDATE plants SET notes = 'Odmiana o pełnych, kremowo-białych kwiatach z pomarańczowym centrum. Intensywny zapach. Kilka kwiatów na łodydze. Naturalizuje się.' WHERE id = 600;

UPDATE plants SET notes = 'Odmiana narcyza poetyckiego o białych płatkach i małej, żółto-czerwonej koronie. Kwitnienie późne (maj). Intensywny zapach. Doskonale naturalizuje się. Nagroda RHS Award of Garden Merit. Klasyka ogrodów wiejskich.' WHERE id = 601;

UPDATE plants SET notes = 'Wyjątkowa odmiana wielokwiatowa – łodyga nosi 15-20 kwiatów. Białe płatki z żółtą koroną. Intensywny zapach. Bardzo efektowna. Naturalizuje się.' WHERE id = 602;

UPDATE plants SET notes = 'Odmiana o żółtych płatkach odgiętych do tyłu i pomarańczowej trąbce. Wczesne kwitnienie (marzec-kwiecień). Doskonale naturalizuje się. Nagroda RHS Award of Garden Merit. Dynamiczny wygląd.' WHERE id = 603;

UPDATE plants SET notes = 'Najpopularniejsza lilia na świecie. Różowe kwiaty z ciemnymi plamkami i białymi brzegami, skierowane w górę. Intensywny zapach. Cebule pozostawiać w gruncie – ściółkować na zimę.' WHERE id = 604;

UPDATE plants SET notes = 'Elegancka odmiana o czysto białych kwiatach 20-25 cm i bardzo intensywnym zapachu. Cebule zimują w gruncie – ściółkować. Odmiana nagradzana. Klasyka do bukietów.' WHERE id = 605;

UPDATE plants SET notes = 'Imponująca odmiana o morelowo-pomarańczowych, trąbkowych kwiatach 20 cm. Intensywny zapach. Bardzo wysoka (180 cm) – wymaga podpór. Cebule zimują w gruncie. Nagroda RHS Award of Garden Merit.' WHERE id = 606;

UPDATE plants SET notes = 'Odmiana hybrydowa Oriental × Trumpet łącząca zalety obu grup. Ciemnoczerwone kwiaty z żółtymi brzegami i ciemnymi plamkami. Aromatyczna. Bardzo odporna.' WHERE id = 607;

UPDATE plants SET notes = 'Odmiana hybrydowa Longiflorum × Asiatic. Białe kwiaty z purpurowym centrum. Wczesne kwitnienie. Odporna. Bez zapachu.' WHERE id = 608;

UPDATE plants SET notes = 'Lilia o turbanowej formie kwiatów – ciemnoczerwone płatki odgięte do tyłu. Toleruje zacienienie. Powolny start (kwitnienie w 2-3 roku), następnie powraca przez wiele lat. Nie przesadzać. Nagroda RHS Award of Garden Merit.' WHERE id = 609;

UPDATE plants SET notes = 'Unikalna odmiana o limonkowo-zielonych kwiatach. Sadzić od kwietnia co 2 tygodnie dla przedłużenia kwitnienia. Wykopywać w październiku, suszyć cebule i przechowywać w chłodzie (5-10°C).' WHERE id = 610;

UPDATE plants SET notes = 'Dramatyczna odmiana o bardzo ciemnych, niemal czarnych kwiatach. Wysokie odmiany wymagają podpór. Wykopywać jesienią.' WHERE id = 611;

UPDATE plants SET notes = 'Delikatna odmiana o białych płatkach z różowymi brzegami i falistymi krawędziami. Romantyczna. Doskonała do bukietów ślubnych. Wykopywać jesienią.' WHERE id = 612;

UPDATE plants SET notes = 'Odmiana o intensywnie purpurowych kwiatach i długich kłosach kwiatowych. Doskonała do cięcia. Wykopywać jesienią.' WHERE id = 613;

UPDATE plants SET notes = 'Klasyczna odmiana z 1949 roku o intensywnie czerwonych, dużych kwiatach. Wciąż popularna. Wykopywać jesienią.' WHERE id = 614;

UPDATE plants SET notes = 'Odmiana o białych, kulistych kwiatostanach 10-15 cm na wysokich łodygach. Naturalizuje się. Liście więdną przed kwitnieniem – sadzić za bylinami. Przyciąga pszczoły.' WHERE id = 615;

UPDATE plants SET notes = 'Imponująca odmiana o ogromnych, purpurowych kulach 15-20 cm na bardzo wysokich łodygach (150 cm). Bardzo efektowna. Naturalizuje się. Przyciąga pszczoły.' WHERE id = 616;

UPDATE plants SET notes = 'Wyjątkowa odmiana o eksplozyjnych kwiatostanach 30-40 cm średnicy – szypułki różnej długości tworzą efekt fajerwerku. Suszone kwiaty bardzo ozdobne. Nagroda RHS Award of Garden Merit.' WHERE id = 617;

UPDATE plants SET notes = 'Odmiana o metalicznie połyskujących, gwiaździstych kwiatostanach 20-25 cm. Suszone kwiaty zachowują się przez wiele lat. Naturalizuje się. Nagroda RHS Award of Garden Merit. Roślina architektoniczna.' WHERE id = 618;

UPDATE plants SET notes = 'Odmiana o owalnych główkach 2-3 cm zmieniających barwę z zielonej na bordową. Najlepsza w masowych nasadzeniach (50-100 sztuk). Naturalizuje się. Przyciąga pszczoły.' WHERE id = 619;

UPDATE plants SET notes = 'Odmiana o dużych, purpurowych kwiatach. Wczesne kwitnienie – pierwszy pokarm dla pszczół. Doskonale naturalizuje się. Sadzić masowo (ponad 50 sztuk). Nie przycinać liści po kwitnieniu.' WHERE id = 620;

UPDATE plants SET notes = 'Wczesna odmiana (luty-marzec) o kremowych kwiatach z żółtym gardłem. Aromatyczna. Doskonale naturalizuje się. Nagroda RHS Award of Garden Merit. Miniaturowa.' WHERE id = 621;

UPDATE plants SET notes = 'Odmiana o perłowo-niebieskich kwiatach. Wczesne kwitnienie. Naturalizuje się. Masowe nasadzenia tworzą efekt niebieskiego dywanu. Nagroda RHS Award of Garden Merit.' WHERE id = 622;

UPDATE plants SET notes = 'Odmiana o purpurowych kwiatach i intensywnym zapachu. Pierwszy rok kwitnienia daje pełne kwiatostany, w kolejnych mniejsze, ale roślina powraca. Doskonała do pędzenia w doniczkach.' WHERE id = 623;

UPDATE plants SET notes = 'Klasyczna holenderska odmiana o porcelanowo-niebieskich kwiatach. Intensywny zapach. Doskonała do pędzenia w doniczkach.' WHERE id = 624;

UPDATE plants SET notes = 'Odmiana o ciemnoniebieskich, pachnących gronach. Naturalizuje się bardzo ekspansywnie – może być inwazyjna. Kontrolować rozrost lub naturalizować na łąkach.' WHERE id = 625;

UPDATE plants SET notes = 'Wbrew nazwie nie pochodzi z Peru (region Morza Śródziemnego). Imponujące, stożkowe kwiatostany złożone z 50-100 ciemnoniebieskich kwiatków. Bardzo efektowny. W Polsce wymaga grubego ściółkowania na zimę.' WHERE id = 626;

UPDATE plants SET notes = 'Odmiana holenderskiego irysa cebulowego o purpurowych płatkach z żółtym paskiem. Doskonała do cięcia. Preferuje suche warunki po kwitnieniu.' WHERE id = 627;

UPDATE plants SET notes = 'Miniaturowy irys siatkowaty o ciemnoniebieskich kwiatach z żółtą plamą. Bardzo wczesne kwitnienie (luty-marzec). Zapach fiołków. Naturalizuje się na suchych stanowiskach. Nagroda RHS Award of Garden Merit.' WHERE id = 628;

UPDATE plants SET notes = 'Unikalna odmiana o perłowo-niebieskich kwiatach z żółto-zielonymi wzorami. Bardzo wczesne kwitnienie (luty). Aromatyczna. Nagroda RHS Award of Garden Merit. Delikatna, elegancka.' WHERE id = 629;

UPDATE plants SET notes = 'Rodzimy gatunek o białych dzwoneczkach z zielonymi plamkami. Większa niż przebiśnieg. Wczesne kwitnienie (luty-marzec). Preferuje wilgotne stanowiska. Naturalizuje się. Delikatny zapach.' WHERE id = 630;

UPDATE plants SET notes = 'Gatunek o intensywnie ciemnoniebieskich, zwisających dzwoneczkach. Wczesne kwitnienie (marzec-kwiecień). Naturalizuje się bardzo ekspansywnie, również z samosiewu. Masowe nasadzenia tworzą efekt niebieskiego dywanu.' WHERE id = 631;

UPDATE plants SET notes = 'Miniaturowa odmiana o drobnych kwiatach i delikatnych liściach. Samooczyszczająca – nie wymaga usuwania zwiędłych kwiatów. Odpędza szkodniki. Liście jadalne o cytrynowym smaku. Rozmnaża się z samosiewu.' WHERE id = 632;

UPDATE plants SET notes = 'Odmiana o rozpościerającym się pokroju. Samooczyszczająca. Wymagająca pod względem nawożenia – zasilać co tydzień. Obficie podlewać. Bardziej odporna na deszcz niż tradycyjne petunie. Doskonała do pojemników.' WHERE id = 633;

UPDATE plants SET notes = 'Najpopularniejsza petunia w Polsce. Samooczyszczająca, obficie kwitnąca. Bardzo wymagająca pod względem nawożenia i podlewania – zasilać co tydzień, podlewać codziennie. Odporna na deszcz.' WHERE id = 634;

UPDATE plants SET notes = 'Odmiana o drobnych kwiatach podobnych do miniaturowych petunii. Samooczyszczająca. Bardzo wymagająca pod względem nawożenia – zasilać co tydzień. Wrażliwa na chlorozę – stosować nawozy z żelazem. Optymalne pH 5,5-6,0.' WHERE id = 635;

UPDATE plants SET notes = 'Klasyczna roślina balkonowa o błyszczących liściach podobnych do bluszczu. Odporna na okresową suszę. Usuwać zwiędłe kwiaty. Można przezimować w chłodnym pomieszczeniu (5-10°C).' WHERE id = 636;

UPDATE plants SET notes = 'Klasyczna pelargonia o kulistych kwiatostanach. Odporna na suszę. Usuwać zwiędłe kwiatostany. Można przezimować w chłodnym pomieszczeniu. Ciąć wiosną dla lepszego krzewienia.' WHERE id = 637;

UPDATE plants SET notes = 'Roślina sezonu chłodnego – kwitnie wiosną (marzec-czerwiec) i jesienią (wrzesień do mrozu). Usuwanie przekwitłych kwiatów przedłuża kwitnienie. Toleruje lekkie przymrozki. Siew w sierpniu daje jesienne kwitnienie, w lutym – wiosenne.' WHERE id = 638;
