-- Naprawa opisów roślin - Batch 5 (ID 739-838)
-- Profesjonalny, spójny styl polski

UPDATE plants SET notes = 'Mangold ozdobny o kolorowych łodygach: żółtych, czerwonych, pomarańczowych, różowych i białych. Ozdobny i jadalny. Siew kwiecień-lipiec. Zbierać liście zewnętrzne – roślina rośnie dalej. Młode liście na surowo, starsze gotowane.' WHERE id = 739;

UPDATE plants SET notes = 'Bylina bardzo ekspansywna. Sadzić korzenie skośnie w kwietniu lub październiku. Zbierać korzenie październik-listopad lub marzec-kwiecień – mróz zwiększa ostrość. Sadzić w ograniczeniu lub wydzielonym miejscu – bardzo trudny do usunięcia. Trzeć świeży.' WHERE id = 740;

UPDATE plants SET notes = 'Japońska sałata o pierzastych liściach i łagodnym, musztardowym smaku. Siew marzec-kwiecień i sierpień-wrzesień (unika upałów). Zbierać liście zewnętrzne lub całe młode rozetki. Dobra do dań smażonych. Toleruje chłód.' WHERE id = 741;

UPDATE plants SET notes = 'Izraelska odmiana o zielono-żółtej skórce z paskami i pomarańczowym, aromatycznym miąższu. W Polsce uprawiać pod osłonami lub w tunelu. Rozsada kwiecień-maj, wysadzać w czerwcu w cieple. Ograniczyć do 3-4 owoców na roślinie. Obficie podlewać do zawiązania owoców, potem ograniczyć dla lepszego smaku. Zbiór sierpień-wrzesień gdy owoc pachnie.' WHERE id = 742;

UPDATE plants SET notes = 'Mała odmiana idealna dla polskich warunków. Okrągłe owoce 2-4 kg o ciemnozielonej skórce i czerwonym miąższu. Uprawiać pod osłonami lub folią. Rozsada w maju, wysadzać w czerwcu (powyżej 20°C). Obficie podlewać. Ograniczyć do 2-3 owoców. Zbiór sierpień-wrzesień gdy łodyga brązowa i pukanie brzmi głucho. Pod owoce podkładać podkładki.' WHERE id = 743;

UPDATE plants SET notes = 'Olbrzymia tykwa 30-100 kg o różowo-pomarańczowej barwie. Tradycyjnie paszowa, ale jadalna (do zup, purée). Wymaga dużej przestrzeni. Siew w maju. Pozostawić 1-2 owoce. Intensywnie nawozić. Podkładki pod owoce. Zbiór wrzesień-październik. Przechowalna przez wiele miesięcy.' WHERE id = 744;

UPDATE plants SET notes = 'Dynia talerzykowa o charakterystycznym żółtym kształcie przypominającym UFO. Odmiana krzaczasta mieszańcowa. Zbierać młode 8-12 cm – najlepszy smak. Siew w maju bezpośrednio do gruntu. Regularnie podlewać. Zbierać często – pobudza dalsze plonowanie.' WHERE id = 745;

UPDATE plants SET notes = 'Włoska cukinia o jasnozielonych paskach na ciemnozielonym tle i orzechowym smaku. Zbierać młode 15-20 cm. Siew w maju. Bardzo plenna. Kwiaty męskie jadalne – doskonałe faszerowane.' WHERE id = 746;

UPDATE plants SET notes = 'Francuska odmiana soczewicy o drobnych, ciemnozielonych ziarnach i wyrazistym aromacie. Siew kwiecień-maj. Nie wymaga podpór – niska. Toleruje suszę. Zbiór sierpień-wrzesień gdy strąki brązowe. Suszyć rośliny całe, młócić. Przechowywać w suchym miejscu. Wiąże azot – dobry przedplon.' WHERE id = 747;

UPDATE plants SET notes = 'Odmiana o dużych, kremowych ziarnach. Trudna w polskich warunkach – wymaga długiego, ciepłego sezonu. Siew w maju (temperatura gleby powyżej 15°C). Toleruje suszę dobrze. Nie podlewać nadmiernie – grozi chorobami grzybowymi. Zbiór wrzesień-październik gdy strąki brązowe. Młócić suszone rośliny. Na hummus, falafel.' WHERE id = 748;

UPDATE plants SET notes = 'Wbrew nazwie nie jest prawdziwym szpinakiem. Grube, mięsiste liście nie wybijają w upały – idealny zamiennik szpinaku latem. Siew maj-czerwiec. Zbierać młode wierzchołki przez cały sezon – roślina rośnie dalej. Toleruje suszę i upały lepiej niż szpinak. Lekko słony smak – wymagaedkrótszego gotowania.' WHERE id = 749;

UPDATE plants SET notes = 'Odmiana o złotożółtych, mięsistych liściach. Soczysta, kwaśna. Bardzo odporna na suszę. Rośnie na słabej glebie. Siew maj-lipiec. Zbierać młode wierzchołki – roślina rośnie dalej. Rozmnaża się z samosiewu – może być ekspansywna. Bogata w kwasy omega-3 i witaminy. Jadalna surowa.' WHERE id = 750;

UPDATE plants SET notes = 'Mangold o bardzo szerokich, białych łodygach i ogromnych liściach. Łodygi gotować oddzielnie – wymagają dłuższego czasu. Liście przygotowywać jak szpinak. Siew kwiecień-lipiec. Zbierać liście zewnętrzne – roślina rośnie dalej przez cały sezon. Mrozoodporna do -5°C. Ciągły plon.' WHERE id = 751;

UPDATE plants SET notes = 'Bylina o dużych, okrągłych, srebrzystoszarych liściach. Nie kwitnie – więcej liści do zbioru. Zimuje w Polsce (do -20°C z okryciem). Zbierać liście przez cały rok. Przycinać wiosną. Nie podlewać nadmiernie. Intensywny aromat. Używać świeżą lub suszoną.' WHERE id = 752;

UPDATE plants SET notes = 'Bylina zimozielona, bardzo mrozoodporna (do -25°C). Aromatyczne liście. Zbierać wierzchołki przez cały rok – najbardziej aromatyczne przed kwitnieniem. Przycinać po kwitnieniu. Nie podlewać. Preferuje suche, słoneczne stanowisko. Pszczoły bardzo lubią kwiaty.' WHERE id = 753;

UPDATE plants SET notes = 'Bylina o intensywnym smaku – grecka odmiana. Bardzo aromatyczna. Zimuje w Polsce. Zbierać przed kwitnieniem (lipiec) na suszenie. Przycinać po kwitnieniu. Rozrasta się intensywnie korzeniami. Preferuje suche stanowisko. Do pizzy i makaronów.' WHERE id = 754;

UPDATE plants SET notes = 'Przyprawa o słodkim, delikatnym smaku. Jednoroczna w Polsce – mróz zabija. Siew kwiecień-maj lub sadzonki w maju. Zbierać wierzchołki przed kwitnieniem. Suszyć wiązkami. Klasyka polskiej kuchni – popularna przyprawa do kiełbas i mięs.' WHERE id = 755;

UPDATE plants SET notes = 'Najbardziej mrozoodporna odmiana rozmarynu – zimuje w Polsce do -20°C z okryciem. Igłowate, aromatyczne liście. Zbierać gałązki przez cały rok. Przycinać wiosną. Nie podlewać. Może rosnąć w doniczce – bezpieczniej zabierać do domu na zimę. Wolny wzrost.' WHERE id = 756;

UPDATE plants SET notes = 'Bylina o anyżowym aromacie. Rozmnażać wyłącznie przez sadzonki lub podział – nasiona są bezpłodne. Zimuje w Polsce. Zbierać młode listki od maja do września. Przycinać przed zimą. Dzielić kępy co 3-4 lata. Klasyka kuchni francuskiej (sos béarnaise).' WHERE id = 757;

UPDATE plants SET notes = 'Bylina dorastająca do imponujących rozmiarów. Smak przypomina przyprawę Maggi. Pierwszy rok nie zbierać – roślina buduje system korzeniowy. Od drugiego roku zbierać młode liście od kwietnia do października. Usuwać kwiaty dla większej ilości liści. Zimuje doskonale. Intensywny aromat – używać oszczędnie. Suszone korzenie również jadalne.' WHERE id = 758;

UPDATE plants SET notes = 'Włoska odmiana o długich, ciemnozielonych, pomarszczonych liściach. Słodszy niż zwykły jarmuż. Siew czerwiec-lipiec (jesienny) lub marzec-kwiecień. Zbierać dolne liście – roślina rośnie dalej. Mróz poprawia smak. Bardzo mrozoodporny (-15°C). Bogaty w składniki odżywcze.' WHERE id = 759;

UPDATE plants SET notes = 'Odmiana mieszańcowa o purpurowoczerwonych, karbowanych liściach. Ozdobna i jadalna. Siew czerwiec-lipiec. Mróz intensyfikuje kolor. Bardzo dekoracyjna w ogrodzie jesienią i zimą. Zbierać młode liście. Mrozoodporna do -15°C.' WHERE id = 760;

UPDATE plants SET notes = 'Holenderska odmiana o większych, ciemnozielonych liściach niż zwykły seler naciowy. Aromatyczny. Rozsada marzec-kwiecień, wysadzać w maju. Regularnie podlewać. Zbierać liście zewnętrzne. Roślina dwuletnia – zimuje. Doskonała do mrożenia.' WHERE id = 761;

UPDATE plants SET notes = 'Odmiana o anyżowym smaku, różna od słodkiej bazylii. Ciemnozielone liście, fioletowe łodygi i kwiaty. Siew w maju lub z rozsady. Wysadzać w czerwcu w cieple. Obcinać wierzchołki dla krzewienia. Usuwać kwiaty dla większej ilości liści. Do kuchni tajskiej i wietnamskiej. Mróz zabija.' WHERE id = 762;

UPDATE plants SET notes = 'Odmiana o cytrynowym aromacie i jasnozielonych liściach. Siew w maju lub z rozsady. Temperatura powyżej 18°C. Obcinać wierzchołki. Usuwać kwiaty. Doskonała do ryb, deserów i herbat. Delikatniejsza niż słodka bazylia. Jednoroczna.' WHERE id = 763;

UPDATE plants SET notes = 'Odmiana krymska o ciemnofioletowo-brązowych owocach 200-500 g i słodko-słonym smaku. Rozsada marzec-kwiecień, grunt po mrozach. Prowadzić z podporą. Usuwać pędy boczne. Regularnie podlewać, unikając zamoknięcia. Idealny do sałatek. Średniowczesny.' WHERE id = 764;

UPDATE plants SET notes = 'Odmiana o zielonożółtych owocach 80-100 g z ciemnymi pasami. Dojrzałe owoce są delikatnie miękkie. Pikantny, orzeźwiający smak. Rozsada marzec-kwiecień. Prowadzić z podporą. Usuwać pędy boczne. Regularnie podlewać. Wczesny. Ozdoba sałatek.' WHERE id = 765;

UPDATE plants SET notes = 'Klasyczna odmiana do przetwórstwa o śliwkowych, mięsistych owocach 100-150 g z małą ilością nasion. Odporna na werticiliozę i fuzariozę (VF). Rozsada marzec-kwiecień. Podpora wskazana. Regularnie podlewać. Idealna do sosów, przecierów i suszenia. Plenna.' WHERE id = 766;

UPDATE plants SET notes = 'Odmiana o czerwono-pomarańczowych paskach. Owoce 60-90 g o słodko-kwaśnym smaku. Wczesna i plenna. Rozsada marzec-kwiecień. Prowadzić z podporą. Usuwać pędy boczne. Toleruje chłód – dobra do tuneli. Ozdobna na talerzu. Bardzo wczesna.' WHERE id = 767;

UPDATE plants SET notes = 'Odmiana o słodkich, delikatnych, walcowatych korzeniach 15-18 cm z tępo zakończonym końcem. Siew marzec-lipiec bezpośrednio do gruntu. Gleba bez kamieni i obornika. Przerzedzić do 5-8 cm. Nie pozostawiać w gruncie zbyt długo – drewnieją. Młode są najsłodsze. Typowa odmiana francuska.' WHERE id = 768;

UPDATE plants SET notes = 'Odmiana o długich, cylindrycznych korzeniach 20-25 cm z tępym zakończeniem. Siew kwiecień-czerwiec bezpośrednio do gruntu. Gleba głęboka, bez kamieni. Przerzedzić. Wysoka zawartość karotenu. Dobra do przechowywania. Średniopóźna.' WHERE id = 769;

UPDATE plants SET notes = 'Włoska odmiana o koncentrycznych, biało-różowych pasach w przekroju. Łagodny, słodki smak. Siew kwiecień-lipiec bezpośrednio lub z rozsady. Przerzedzić. Młode liście jadalne. Najpiękniejszy surowy – po gotowaniu blednie.' WHERE id = 770;

UPDATE plants SET notes = 'Klasyczna odmiana o okrągłych, ciemnoczerwonych korzeniach średniej wielkości. Siew kwiecień-lipiec bezpośrednio. Przerzedzić do 10-15 cm. Regularnie podlewać. Dobra do gotowania, pieczenia i kiszenia. Uniwersalna. Plenna.' WHERE id = 771;

UPDATE plants SET notes = 'Odmiana o stożkowych korzeniach 20-25 cm, kremowobiałych. Siew marzec-maj bezpośrednio do gruntu. Głęboka gleba. Mróz poprawia smak. Zbierać po przymrozkach lub wiosną. Doskonały do zup i pieczenia. Niedocenione warzywo.' WHERE id = 772;

UPDATE plants SET notes = 'Odmiana o dużych, gładkich, okrągłych bulwach 500-1000 g bez dziur. Rozsada w marcu w szklarni, wysadzać w czerwcu. Regularnie podlewać. Odgarniać ziemię od bulwy. Zbiór październik-listopad przed mrozami. Idealny do zup, surówek i frytek. Przechowywać w piasku.' WHERE id = 773;

UPDATE plants SET notes = 'Odmiana o śnieżnobiałej główce, kompaktowa, równomierna 1-1,5 kg. Rozsada kwiecień-maj, wysadzać czerwiec-lipiec. Regularnie podlewać i nawozić. Przykryć liściem gdy główka ma 5-8 cm – zachowa biel. Zbierać na bieżąco. Wrażliwa na upały. Odmiana preferująca chłód.' WHERE id = 774;

UPDATE plants SET notes = 'Klasyczna włoska odmiana o ciemnozielonej główce centralnej i bocznych różyczkach. Rozsada kwiecień-czerwiec, wysadzać czerwiec-lipiec. Regularnie podlewać i nawozić. Zbierać główną różyczkę wcześnie – pobudzi boczne. Plenny przez całe lato. Toleruje upały.' WHERE id = 775;

UPDATE plants SET notes = 'Odmiana o zwartych główkach 2-3 cm na łodydze, słodkich po przymrozkach. Rozsada maj-czerwiec, wysadzać w lipcu. Podpora wskazana. Regularnie podlewać i nawozić. Stopniowo odłamywać dolne liście. Zbierać od dołu po przymrozkach. Mrozoodporna.' WHERE id = 776;

UPDATE plants SET notes = 'Odmiana o cylindrycznych, zwartych główkach 1-2 kg, żółtozielonych. Siew lipiec-sierpień bezpośrednio lub z rozsady (krótki dzień). Regularnie podlewać – nie dopuścić do stresu. Delikatna, chrupiąca, słodka. Doskonała surowa i duszona. Odmiana jesienna.' WHERE id = 777;

UPDATE plants SET notes = 'Włoska odmiana o ciemnozielonych, długich, pomarszczonych liściach. Rozsada maj-lipiec, wysadzać czerwiec-sierpień. Zbierać młode liście od dołu. Mróz poprawia smak. Mrozoodporna do -15°C. Bogata w składniki odżywcze. Piękna i smaczna.' WHERE id = 778;

UPDATE plants SET notes = 'Odmiana o długich, białych, cylindrycznych łodygach 30-40 cm. Rozsada marzec-kwiecień, wysadzać czerwiec-lipiec w bruzdy. Okopywać ziemią dla wydłużenia białej części. Regularnie podlewać i nawozić. Mrozoodporny. Zbiór od września do marca. Łagodny smak cebuli.' WHERE id = 779;

UPDATE plants SET notes = 'Bylina tworząca kępy cienkich, rurkowych liści. Siew kwiecień-maj lub podział kęp wiosną/jesienią. Ciąć 5 cm nad ziemią – odrastają. Regularnie podlewać. Dzielić co 3-4 lata. Kwiaty jadalne i ozdobne. Łagodny smak cebuli. Mrozoodporny.' WHERE id = 780;

UPDATE plants SET notes = 'Niezwykła cebula tworząca cebulki na szczycie łodygi zamiast kwiatów, które same się sadzą. Bylina. Sadzić bulwy wiosną lub jesienią. Ciąć zielone pióra na bieżąco. Cebulki jadalne – ostre. Niezniszczalna.' WHERE id = 781;

UPDATE plants SET notes = 'Bardzo szybka roślina – kiełki w 2-3 dni, zbiór po 7-14 dniach. Siew przez cały rok w domu na wacie, w ogrodzie od marca do września. Siać gęsto, często podlewać. Ciąć nożyczkami na wysokości 5 cm. Ostry, pikantny smak. Idealna na kanapki i do jajek.' WHERE id = 782;

UPDATE plants SET notes = 'Szybka odmiana o zębkowanych liściach i orzechowo-pikantnym smaku. Siew od marca do września co 2-3 tygodnie dla ciągłości zbiorów. Siać gęsto, przerzedzić. Ciąć 5 cm nad ziemią – odrastają 2-3 razy. W upały gorzknie i kwitnie. Jesień jest najlepszą porą.' WHERE id = 783;

UPDATE plants SET notes = 'Odmiana o delikatnych, masłowych liściach i jasnych główkach. Siew od marca do sierpnia co 2 tygodnie. Z rozsady lub bezpośrednio. Regularnie podlewać – nie dopuścić do kwitnienia. Zbierać rano dla chrupkości. Wczesna odmiana odporna na przedwczesne kwitnienie.' WHERE id = 784;

UPDATE plants SET notes = 'Odmiana o dużych, zwartych główkach i jasnych, chrupiących liściach. Siew kwiecień-lipiec z rozsady lub bezpośrednio. Regularnie podlewać – brak wilgoci powoduje goryczkę. Zbierać rano. Toleruje upały. Długa trwałość po zbiorze. Klasyka do burgerów.' WHERE id = 785;

UPDATE plants SET notes = 'Odmiana o szerokich liściach i gorzkawo-orzechowym smaku. Siew lipiec-sierpień (krótki dzień). Z rozsady lub bezpośrednio. Bielić 2 tygodnie przed zbiorem (związać lub przykryć) – zmniejsza goryczkę. Mrozoodporna. Jesienny delikates.' WHERE id = 786;

UPDATE plants SET notes = 'Włoska cykoria o ciemnoczerwonych główkach z białymi żyłkami i gorzkim, wykwintnym smaku. Siew lipiec-sierpień w chłodzie. Mróz poprawia słodycz. Zbierać po przymrozkach. Grillowana jest wyśmienita. Włoski skarb kulinarny.' WHERE id = 787;

UPDATE plants SET notes = 'Odmiana o mięsistych, dużych, ciemnozielonych, błyszczących liściach. Siew marzec-kwiecień i sierpień-wrzesień (preferuje chłód). Regularnie podlewać. Zbierać liście zewnętrzne lub całą roślinę. Upał powoduje szybkie kwitnienie. Bogata w żelazo.' WHERE id = 788;

UPDATE plants SET notes = 'Odmiana o złotych łodygach i jasnozielonych liściach. Siew kwiecień-sierpień bezpośrednio lub z rozsady. Zbierać liście zewnętrzne – roślina rośnie przez cały sezon. Ozdobna i smaczna. Łagodniejsza niż szpinak. Mrozoodporna.' WHERE id = 789;

UPDATE plants SET notes = 'Bardzo ozdobna odmiana o kolorowych łodygach: różowych, żółtych, pomarańczowych, czerwonych i białych. Siew kwiecień-sierpień. Zbierać młode liście. Piękna w ogrodzie ozdobnym. Nagroda RHS Award of Garden Merit.' WHERE id = 790;

UPDATE plants SET notes = 'Odmiana o płaskich strąkach bez włókien, jadalnych razem z łupiną. Słodkie, chrupiące. Siew marzec-maj bezpośrednio do gruntu. Podpora 120 cm. Regularnie zbierać – pobudza plonowanie. Wiąże azot. Doskonała do dań smażonych. Wytrzymała.' WHERE id = 791;

UPDATE plants SET notes = 'Odmiana karłowa niewymagająca podpór. Zielone, okrągłe strąki 13-15 cm bez włókien. Siew maj-lipiec bezpośrednio w cieple. Regularnie zbierać młode – plenna przez 3-4 tygodnie. Wiąże azot. Wczesna i niezawodna.' WHERE id = 792;

UPDATE plants SET notes = 'Odmiana karłowa o fioletowych strąkach 15 cm, niewymagająca podpór. Siew maj-lipiec w cieple. Łatwe do zauważenia przy zbiorze. Po ugotowaniu zieleniejące. Ozdobna i smaczna. Wczesna.' WHERE id = 793;

UPDATE plants SET notes = 'Włoska odmiana o żebrowanych, jasnozielonych owocach z ciemnymi pasami i orzechowym smaku. Siew w maju bezpośrednio lub z rozsady w kwietniu. Regularnie podlewać i nawozić. Zbierać 15-20 cm. Kwiaty jadalne. Plenna.' WHERE id = 794;

UPDATE plants SET notes = 'Francuska odmiana o okrągłych, jasnych owocach idealnych do faszerowania. Siew w maju bezpośrednio lub z rozsady w kwietniu. Regularnie podlewać u podstawy. Zbierać gdy mają 8-10 cm średnicy (rozmiar piłki tenisowej). Doskonała do nadziewania.' WHERE id = 795;

UPDATE plants SET notes = 'Odmiana o nietypowym kształcie – spłaszczona z fałdowanymi brzegami. Biała. Siew w maju bezpośrednio lub z rozsady w kwietniu. Regularnie podlewać. Zbierać małe 5-8 cm – najsmaczniejsze. Duże powyżej 15 cm są twarde i tylko dekoracyjne.' WHERE id = 796;

UPDATE plants SET notes = 'Odmiana o długich, ciemnozielonych owocach cylindrycznych 25-30 cm z jasnymi paskami. Siew w maju bezpośrednio lub z rozsady w kwietniu. Regularnie podlewać i nawozić. Zbierać młode. Uniwersalna. Bardzo plenna.' WHERE id = 797;

UPDATE plants SET notes = 'Odmiana o gruszkowatym kształcie 1-2 kg i beżowej, gładkiej skórce. Siew w maju bezpośrednio lub z rozsady w kwietniu. Wymaga dużo miejsca. Regularnie podlewać i nawozić. Zbierać gdy łodyga sucha. Przechowalna 3-6 miesięcy. Słodki, maślany smak. Bogata w składniki odżywcze.' WHERE id = 798;

UPDATE plants SET notes = 'Odmiana rekordowa – rekord świata ponad 1200 kg. Pomarańczowa, okrągła, ogromna. Siew w maju bezpośrednio. Wymaga dużo przestrzeni, nawozu i wody. Pozostawić 1 owoc. Rośnie kilka kg dziennie. Na konkursy dyń.' WHERE id = 799;

UPDATE plants SET notes = 'Niezwykła odmiana – po upieczeniu miąższ rozdziela się na włókna przypominające makaron. Żółta, owalna 2-3 kg. Siew w maju bezpośrednio. Regularnie podlewać i nawozić. Zbierać gdy żółta i twarda. Przechowalna 2-3 miesiące. Niskokaloryczna alternatywa dla makaronu.' WHERE id = 800;

UPDATE plants SET notes = 'Słodkie ziemniaki o pomarańczowych bulwach 300-600 g. Sadzonki w maju w cieple. W Polsce wymaga tunelu. Regularnie podlewać. Zbiór wrzesień-październik przed mrozami. Sezonować 2 tygodnie dla słodszego smaku. Bogaty w składniki odżywcze.' WHERE id = 801;

UPDATE plants SET notes = 'Bylina bardzo ekspansywna o ostrym, białym korzeniu. Sadzenie kawałków korzeni marzec-kwiecień. Głęboko kopać – pozostawione fragmenty odrastają. Zbierać korzenie październik-marzec (po mrozie najostrzejszy). Uwaga: bardzo trudny do usunięcia. Zawiera glukozynolany.' WHERE id = 802;

UPDATE plants SET notes = 'Roślina śródziemnomorska o dużych, zielonych pąkach kwiatowych 10-15 cm. Rozsada marzec-kwiecień, wysadzać w maju. Zimowanie: okopać i osłonić (strefa 7) lub wykopać. Zbierać główki gdy zwarte, przed kwitnieniem. Młode są najlepsze. Ozdobna.' WHERE id = 803;

UPDATE plants SET notes = 'Odmiana o anyżowej bulwie. Białe, spłaszczone, chrupiące bulwy 8-10 cm. Siew lipiec-sierpień (krótki dzień – w długim dniu wybija). Regularnie podlewać – sucho powoduje kwitnienie. Okopywać bulwę. Zbierać przed mrozami. Słodki smak anyżu. Wykwintna.' WHERE id = 804;

UPDATE plants SET notes = 'Włoska odmiana o falistych, czerwono-zielonych liściach i luźnej różetce. Siew marzec-wrzesień co 2 tygodnie. Zbierać liście zewnętrzne – roślina rośnie dalej. Toleruje upały. Ozdobna i smaczna.' WHERE id = 805;

UPDATE plants SET notes = 'Odmiana o falistych, jasnozielonych liściach i luźnej różetce. Siew marzec-wrzesień co 2 tygodnie. Zbierać liście zewnętrzne – roślina rośnie dalej. Toleruje upały. Słodki smak. Podobna do Lollo Rosso, ale zielona.' WHERE id = 806;

UPDATE plants SET notes = 'Miniaturowa odmiana rzymska o małych, zwartych, wydłużonych główkach 12-15 cm. Chrupiące, słodkie serduszko. Siew marzec-sierpień. Regularnie podlewać. Idealna na grilla całą lub do sałatki Caesar. Nagroda RHS Award of Garden Merit.' WHERE id = 807;

UPDATE plants SET notes = 'Odmiana o długich, czarnych korzeniach 25-35 cm z białym miąższem. Siew kwiecień-maj bezpośrednio do gruntu. Głęboka gleba. Zbiór październik-marzec (mrozoodporna). Gotować ze skórką, potem obierać. Smak przypomina ostrygi. Delikatesowe warzywo.' WHERE id = 808;

UPDATE plants SET notes = 'Odmiana o białym, stożkowym korzeniu 20-25 cm i aromatycznych liściach. Siew marzec-maj bezpośrednio (wolno kiełkuje – ok. 3 tygodnie). Głęboka gleba. Przerzedzić. Zbiór październik-listopad. Korzenie do zup, liście do sałatek. Mrozoodporna.' WHERE id = 809;

UPDATE plants SET notes = 'Młode buraki zbierane z botwinką. Czerwone, małe korzenie 3-5 cm wraz z młodymi listkami. Siew kwiecień-sierpień gęsto. Nie przerzedzać – zbierać całe rośliny. Zbiór gdy korzenie są miniaturowe. Całość jadalna. Wiosenny delikates. Polska tradycja.' WHERE id = 810;

UPDATE plants SET notes = 'Odmiana mieszańcowa o kędzierzawych, ciemnofioletowych liściach. Rozsada maj-lipiec, wysadzać lipiec-sierpień. Zbierać młode liście od dołu. Mróz poprawia smak i kolor. Mrozoodporna do -20°C. Ozdobna jak kwiat.' WHERE id = 811;

UPDATE plants SET notes = 'Odmiana o ciemnozielonych, cylindrycznych, drobno ciernistych owocach 12-15 cm. Siew w maju bezpośrednio lub z rozsady w kwietniu. Podpora wskazana. Regularnie podlewać i nawozić. Zbierać codziennie – młode najlepsze. Bardzo plenna.' WHERE id = 812;

UPDATE plants SET notes = 'Odmiana do korniszonów o ciemnozielonych, gładkich owocach 6-9 cm. Siew w maju bezpośrednio. Podpora. Regularnie podlewać i nawozić. Zbierać codziennie gdy małe. Idealna do kiszenia. Plonuje obficie przez całe lato.' WHERE id = 813;

UPDATE plants SET notes = 'Odmiana krzaczasta niewymagająca podpór. Ciemnozielone, gładkie owoce 18-20 cm. Siew w maju bezpośrednio lub z rozsady. Kompaktowa – idealna na małe ogródki. Regularnie podlewać i nawozić. Zbierać młode. Doskonała do pojemników.' WHERE id = 814;

UPDATE plants SET notes = 'Amerykańska odmiana o ogromnych, płaskich bulwach 500-1000 g i łagodnym smaku jak jabłko. Rozsada w marcu lub sadzonki w kwietniu. Regularnie nawozić. Zaprzestać podlewania gdy natka przywala. Nieprzechowalna – spożywać na bieżąco.' WHERE id = 815;

UPDATE plants SET notes = 'Odmiana o ciemnofioletowych, okrągłych bulwach 200-300 g. Sadzić sadzonki w kwietniu lub siać wczesną wiosną. Regularnie nawozić. Zaprzestać podlewania gdy natka przywala. Dobra do przechowywania. Ozdobna w potrawach.' WHERE id = 816;

UPDATE plants SET notes = 'Polska odmiana jara o białych główkach średniej wielkości 5-8 cm z 10-15 ząbkami. Sadzenie ząbków marzec-kwiecień. Okopywać, pielić. Zbiór lipiec-sierpień gdy liście żółkną. Suszyć w cieniu. Krótsza przechowalność niż czosnek ozimy. Łagodniejszy smak.' WHERE id = 817;

UPDATE plants SET notes = 'Bardzo wczesna odmiana o żółtych, owalnych bulwach 100-150 g. Sadzenie w kwietniu (podkiełkowane). Obsypywać 2-3 razy. Zbiór w lipcu (młode) lub sierpniu. Cienka skóra – ostrożnie kopać. Nieprzechowalna – spożywać na bieżąco. Wyśmienite młode ziemniaki.' WHERE id = 818;

UPDATE plants SET notes = 'Odmiana o owalnych bulwach 150-250 g z żółtą skórą i miąższem. Sadzenie w kwietniu. Obsypywać 2-3 razy. Zbiór sierpień-wrzesień po uschnięciu naci. Dobra przechowalność. Uniwersalna – do wszystkich potraw. Popularna w Niemczech.' WHERE id = 819;

UPDATE plants SET notes = 'Bylina tworząca wysokie kwiaty słoneczników i jadalne bulwy. Sadzenie bulw marzec-kwiecień. Rośnie wszędzie. Uwaga: bardzo ekspansywna – trudna do usunięcia. Zbierać bulwy po mrozie – słodsze. Smak orzechowo-słodki. Zawiera inulinę zamiast skrobi – odpowiednia dla diabetyków.' WHERE id = 820;

UPDATE plants SET notes = 'Odmiana o okrągłych, żółtych korzeniach 8-10 cm i słodkim smaku. Siew kwiecień-maj i lipiec-sierpień bezpośrednio do gruntu. Przerzedzić. Młode liście jadalne. Zbierać gdy mają 6-10 cm. Tradycyjne warzywo. Słodsza od rzepy białej.' WHERE id = 821;

UPDATE plants SET notes = 'Polska odmiana o okrągłych korzeniach 15-20 cm z fioletową górą i żółtym dołem. Siew maj-czerwiec bezpośrednio. Przerzedzić. Mrozoodporna. Zbierać wrzesień-listopad po przymrozkach – słodsza. Przechowywać w piasku. Tradycyjna polska kuchnia.' WHERE id = 822;

UPDATE plants SET notes = 'Egzotyczna roślina o aromatycznych korzeniach białożółtych 15-25 cm, smakujących jak seler z marchewką. W Polsce tylko w tunelu lub donicach. Długi sezon wegetacji. Smak delikatny, skrobiowy. Popularna w Ameryce Południowej. Trudna w uprawie.' WHERE id = 823;

UPDATE plants SET notes = 'Odmiana o złotych jagodach 15-20 mm w papierowych torebkach. Słodko-kwaśny smak. Rozsada marzec-kwiecień, grunt w maju. Rozmnaża się z samosiewu. Zbierać gdy lampiony brązowe i owoc złoty. Owoce opadają same. Plenna. Jadalna surowa.' WHERE id = 824;

UPDATE plants SET notes = 'Roślina o zielonych lub fioletowych owocach 4-6 cm w papierowych lampionach, kleistych pod łupiną. Rozsada marzec-kwiecień, grunt w maju. Podpora. Potrzeba 2 roślin do zapylenia. Zbierać gdy wypełniają lampion. Kwaśny smak. Do zielonej salsy.' WHERE id = 825;

UPDATE plants SET notes = 'Chińska kapusta o białych, chrupiących ogonkach i zielonych liściach. Luźna różetka. Siew kwiecień-maj i lipiec-sierpień (unika upałów). Regularnie podlewać. Zbierać całą roślinę. Szybki wzrost. Do dań smażonych. Kuchnia azjatycka.' WHERE id = 826;

UPDATE plants SET notes = 'Japońska gorczyca o zębkowanych, delikatnych liściach i łagodnie pikantnym smaku. Siew marzec-wrzesień co 2-3 tygodnie. Zbierać młode liście – roślina rośnie dalej. Toleruje chłód. Młode liście po 3 tygodniach. Modna w mieszankach sałat.' WHERE id = 827;

UPDATE plants SET notes = 'Japońska kapusta o okrągłych, gładkich liściach i słodkim smaku bez goryczki. Siew marzec-październik. Toleruje chłód i mróz. Zbierać liście lub całą roślinę. Bogata w wapń. Zdrowsza od szpinaku. Kuchnia japońska.' WHERE id = 828;

UPDATE plants SET notes = 'Roślina o płaskiej różetce ciemnozielonych, łyżeczkowatych liści. Siew marzec-październik. Mrozoodporna do -10°C. Zbierać całą różetkę lub liście. Słodki, delikatny smak. Piękna w ogrodzie. Młode liście po 3 tygodniach.' WHERE id = 829;

UPDATE plants SET notes = 'Roślina o jadalnych liściach i ziarnach. Młode liście jak szpinak, ziarna są pseudozbożem (bezglutenowym). Siew w maju bezpośrednio. Toleruje suszę. Zbierać młode liście lub ziarna z kłosów. Wysokobiałkowa. Ozdobna.' WHERE id = 830;

UPDATE plants SET notes = 'Andyjskie superziarno o białych, czerwonych lub czarnych ziarnach, wysokobiałkowe i bezglutenowe. Siew w maju bezpośrednio. Toleruje chłód i suszę. Zbierać gdy liście opadają. Suszyć, młócić. Płukać saponiny przed spożyciem. Trudna w Polsce – wymaga długiego dnia.' WHERE id = 831;

UPDATE plants SET notes = 'Bylina o słodkim korzeniu 50-100 cm, 50 razy słodszym od cukru. Wymaga ciepła (strefa 8+). Sadzenie korzeni marzec-kwiecień. Głęboka gleba. Zbierać w 3-4 roku. Suszyć. Właściwości lecznicze. Trudna w Polsce.' WHERE id = 832;

UPDATE plants SET notes = 'Unikalna odmiana zielona na zewnątrz, różowa w środku jak arbuz. Okrągła 6-8 cm o łagodnym smaku. Siew kwiecień-maj i sierpień-wrzesień (preferuje chłód). Regularnie podlewać. Zbierać gdy ma 6-8 cm. Przepiękna w przekroju. Ozdobna w sałatkach.' WHERE id = 833;

UPDATE plants SET notes = 'Bardzo wczesna odmiana o białych, gładkich kulkach 5-7 cm – tylko 35 dni do zbioru. Siew marzec-wrzesień co 2 tygodnie. Regularnie podlewać. Zbierać młode. Słodkie, chrupiące, bez włókien. Nagroda AAS. Doskonała surowa.' WHERE id = 834;

UPDATE plants SET notes = 'Odmiana o zielonych strączkach 8-12 cm bez włosków i kolców. Wymaga stałego ciepła powyżej 20°C – w Polsce tunel. Rozsada w maju, grunt w czerwcu. Regularnie podlewać i nawozić. Zbierać młode 8-12 cm codziennie – większe śluzowacieją. Piękne kwiaty.' WHERE id = 835;

UPDATE plants SET notes = 'Roślina o soczystych, mięsistych liściach i czerwonych łodygach. Siew maj-lipiec bezpośrednio. Toleruje suszę. Często występuje jako chwast w ogrodach. Zbierać młode pędy. Kwaśno-słony smak. Bogata w kwasy omega-3. Rozmnaża się z samosiewu.' WHERE id = 836;

UPDATE plants SET notes = 'Wczesna odmiana o okrągłych, jasnozielonych główkach 1-1,5 kg. Rozsada w marcu, grunt kwiecień-maj. Regularnie podlewać i nawozić. Zbierać gdy zwarta czerwiec-lipiec. Nieprzechowalna – spożywać świeżą. Chrupiąca i słodka.' WHERE id = 837;

UPDATE plants SET notes = 'Późna odmiana do kiszenia o twardych, białozielonych główkach 3-5 kg. Rozsada w maju, grunt w lipcu. Regularnie podlewać i nawozić. Mrozoodporna. Zbierać październik-listopad po mrozach – słodsza. Długa przechowalność. Idealna do kiszenia.' WHERE id = 838;
